CREATE TABLE IF NOT EXISTS `miki_shops` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `MarketName` longtext DEFAULT NULL,
  `MarketOwner` longtext DEFAULT NULL,
  `MarketOwnerName` longtext DEFAULT NULL,
  `Price` longtext DEFAULT '0',
  `image` longtext DEFAULT NULL,
  `description` longtext DEFAULT 'Renting a market space can be a strategic step for entrepreneurs looking to establish a retail presence without the commitment of a permanent location. This approach offers increased visibility, cost-efficiency, and flexibility, making it ideal for testing business ideas, attracting customers, and adapting to changing market dynamics. To succeed in this venture, select your market location wisely, create an appealing display, align with peak market days, network with fellow vendors, and actively promote your presence. With the right approach, renting a market space can pave the way to a successful and thriving business.',
  `renttime` longtext DEFAULT NULL,
  `totalearn` longtext DEFAULT '0',
  `money` longtext DEFAULT '0',
  `items` longtext DEFAULT '{}',
  `workers` longtext DEFAULT '{}',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=54 DEFAULT CHARSET=utf8;


